import json
from django.test import TestCase
from rest_framework.reverse import reverse
from faker import Faker

NAMESPACE = 'cms_urls:'


class RegisterApiTestCase(TestCase):
    def setUp(self):
        myFactory = Faker()
        self.full_name = myFactory.name()
        self.email = "test@gmail.com"
        self.address = myFactory.address()
        self.city = "city"
        self.state = myFactory.state()
        self.country = "India"
        self.pincode = "396191"
        self.password = "Test@123"
        self.invalid_password = "test123"

    def test_positive_registration(self):
        url = reverse(NAMESPACE + 'register')
        request = {
            "email_id": self.email,
            "password": self.password,
            "full_name": self.full_name,
            "address": self.address,
            "city": self.city,
            "state": self.state,
            "country": self.country,
            "pincode": self.pincode
        }
        response = self.client.post(url, data=request,
                                    content_type='application/json')
        self.assertEqual(response.status_code, 200)

    def test_negative_invalid_password_registration(self):
        url = reverse(NAMESPACE + 'register')
        request = {
            "email_id": self.email,
            "password": self.invalid_password,
            "full_name": self.full_name,
            "address": self.address,
            "city": self.city,
            "state": self.state,
            "country": self.country,
            "pincode": self.pincode
        }
        response = self.client.post(url, data=request,
                                    content_type='application/json')
        self.assertEqual(response.status_code, 400)


