class LoginApiTestCase(TestCase):
    def setUp(self):
        myFactory = Faker()
        self.full_name = myFactory.name()
        self.email = "test@gmail.com"
        self.address = myFactory.address()
        self.city = "city"
        self.state = myFactory.state()
        self.country = "India"
        self.pincode = "396191"
        self.password = "Test@123"
        url = reverse(NAMESPACE + 'register')
        request = {
            "email_id": self.email,
            "password": self.password,
            "full_name": self.full_name,
            "address": self.address,
            "city": self.city,
            "state": self.state,
            "country": self.country,
            "pincode": self.pincode
        }

        response = self.client.post(url, data=request,
                                    content_type='application/json')
        self.assertEqual(response.status_code, 200)

        url = reverse('api_token_auth')
        request = {
            "username": self.email,
            "password": self.password
        }

        response = self.client.post(url, data=request,
                                    content_type='application/json')
        self.token = response.json().get("token")

    def test_positive_add_content(self):
        url = reverse(NAMESPACE + 'add_content')
        request = {
            "title": "title2",
            "summary": "summary2",
            "body": "body2",
            "categories": "blog_writer",
            "pdf": "data:application/pdf;base64,"

        }
        headers = {
            'Authorization': 'Token ' + self.token,
            'Content-Type': 'application/json'
        }
        response = self.client.post(url, data=request, headers=headers)
        print(response.json())
        self.assertEqual(response.status_code, 200)
